import React, { useState } from "react";
import "../../styles/projects/projects.css";
import bloghub_home from "../../assets/bloghub_home1.png";
import bloghub_blogs from "../../assets/bloghub_blogs.png";
import bloghub_logo from "../../assets/logo_transparent2.png";

export default function Projects() {
	const [name, setName] = useState("blog");

	const style_1 = {
		gridColumn: "2 / -1",
		gridRow: "2 / -1",
		zIndex: "3",
	};

	const style_2 = {
		gridColumn: "1 / 3",
		gridRow: "1 / 3",
		zIndex: "4",
	};

	function changeImg(e) {
		setName(e.target.name);
	}

	return (
		<div className="projects__container">
			<div className="title">
				<h1>Projects</h1>
			</div>
			<div className="title_up">
				<h1>Recent Projects</h1>
			</div>
			<hr />

			<div className="projects">
				<div className="project bloghub">
					<div className="details">
						<div className="project_logo">
							<p className="featured">Featured Project</p>
							{/* <img src={bloghub_logo} alt="" /> */}
							<h2>Bloghub</h2>
						</div>
						<div className="desc">
							<ul>
								<li>
									<p>
										<span>Bloghub</span> is a fully functional blogging website inspired by Medium.
										Users can register, login, and post. Users can sort blogs according to criteria
										or author while it uses CKeditor5 rich text editor.
									</p>
								</li>
								<li>
									<p>
										I used Web Scraping to find and show most popular blogs from several popular
										websites and It is easy to sort blogs depending on website names.
									</p>
								</li>
							</ul>
						</div>
					</div>
					<div className="blank"></div>
					<div className="image">
						<img
							className="home"
							src={bloghub_home}
							style={name === "home" ? style_1 : style_2}
							onClick={changeImg}
							alt=""
						/>
						<img
							className="blog"
							style={name === "blog" ? style_1 : style_2}
							src={bloghub_blogs}
							onClick={changeImg}
							alt=""
						/>
					</div>
				</div>
			</div>
		</div>
	);
}
